const LIMIT_DATE_LINE = 10
const LIMIT_AUTHOR_LINE = 20
const LIMIT_ADDRESS_LINES = 15

window.onload = function () { snow(1) }

function snow(id) {
    var png_sh = Math.floor(Math.random() * 5) + 1
    var pos_x = Math.floor(Math.random() * 98) + 1
    var step = Math.floor(Math.random() * 30) - 15

    var end_x = pos_x + step
    var img = "<img id='snow_" + id + "' style='left: " + pos_x +
        "%; top: -10%; position: fixed;' src='img/snow.gif'>"
    $("#snow").append(img)
    move_show(id, end_x)
    id++;
    if (id > 150){
        id = 0;
    }
    setTimeout("snow(" + id + ");", 150)
}
function move_show(id, end_x) {
    $("#snow_" + id).animate({ top: "120%", left: "" + end_x + "%" }, 20000, function () {
        $("#snow_" + id).empty().remove()
    });
}

formSend.onclick = () => {
    event.preventDefault()
    const addressLine1 = formAddressLine1.value
    const addressLine2 = formAddressLine2.value
    const date = formDate.value
    const author = formAuthor.value

    if (!(addressLine1 && addressLine2 && date && author && formMessage.value)) {
        alert('Есть пустые поля...')
        return
    }

    const message = formMessage.value.split('\n');

    if (addressLine1.length > LIMIT_ADDRESS_LINES || addressLine2.length > LIMIT_ADDRESS_LINES) {
        alert('Строки адреса длинны. Максимум 15 смволов...')
        return
    }

    if (author.length > LIMIT_AUTHOR_LINE) {
        alert('Имя должно быть короче')
        return
    }

    if (date.length > LIMIT_DATE_LINE) {
        alert('Слишком длинная строка даты')
        return
    }

    var docInfo = {

        info: {
            title: 'Письмо Деду Морозу',
            author: author,
            subject: 'Письмо',
            keywords: 'Подарок'
        },

        pageSize: 'A4',
        pageOrientation: 'portrait',//'portrait'
        pageMargins: [50, 50, 30, 60],

        background: [
            {
                image: 'data:image/jpeg;base64,',
                width: 595,
                height: 850
            }
        ],

        content: [

            {
                text: addressLine1,
                fontSize: 14,
                alignment: 'left',
                margin: [0, 80, 40, 0]
            },

            {
                text: addressLine2,
                fontSize: 14,
                alignment: 'left',
                margin: [0, 0, 0, 40]
            },

            {
                text: 'Дорогой Дед Мороз,',
                fontSize: 20,
                alignment: 'center',
                style: 'header',
            },

            {
                text: message,
                fontSize: 14,
                style: 'header',
                alignment: 'justify',
                margin: [0, 40, 0, 20]
            },

            {
                text: author,
                fontSize: 14,
                alignment: 'left',
                margin: [0, 0, 0, 0]
            },

            {
                text: date,
                fontSize: 14,
                alignment: 'right',
                margin: [0, 0, 0, 0]
            },

        ]
    }
    pdfMake.createPdf(docInfo).open()
};